import React, { Component } from "react";

import IconSvg from "../../component/SVG/Svg";
import { Transition, animated } from "react-spring/renderprops";

const ServiceList = [
  {
    icon: "ECO",
    title: "Espuma Ecologica",
    description:
      "There are many variations of passages of Lorem Ipsum available, but the majority have suffered."
  },
  {
    icon: "ROTO",
    title: "Sin Flúor",
    description:
      "There are many variations of passages of Lorem Ipsum available, but the majority have suffered."
  },
  {
    icon: "AGUA",
    title: "Sin Solvente",
    description:
      "There are many variations of passages of Lorem Ipsum available, but the majority have suffered."
  }
];

class ServiceOne extends Component {
  state = { show: true };
  toggle = e => this.setState(state => ({ show: !state.show }));
  render() {
    return (
      <React.Fragment>
        <div className="row">
          {ServiceList.map((val, i) => (
            <div className="col-lg-4 col-md-6 col-sm-6 col-12" key={i}>
              <div className="service service__style--1">
                <div className="icon">
                  <IconSvg
                    name={val.icon}
                    className="animated fadeIn d-block"
                    sstyle={{ position: "relative" }}
                  />
                  <Transition
                    native
                    items={this.state.show}
                    from={{
                      position: "relative",
                      overflow: "hidden",
                      height: 0
                    }}
                    enter={[{ height: "auto" }]}
                    leave={{ height: "auto" }}
                  >
                    {show => show && (props => <animated.div></animated.div>)}
                  </Transition>
                </div>
                <div className="content">
                  <h4 className="title">{val.title}</h4>
                  <p>{val.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </React.Fragment>
    );
  }
}
export default ServiceOne;
